class Solution {
public:
    vector preOrder;
    vector inOrder;

    void helper(TreeNode* root,int preLeftIndex,int preRightIndex,int inLeftIndex,int inRightIndex)
    {
        //在中序遍历中寻找root
        if(preRightIndex-preLeftIndex == 0 || root == NULL ) return;
        int cnt = 0;
        for(int i=inLeftIndex ;ival)
            {
                cnt++;
            }
            else
            {
                break;
            }
