        if(preorder.size() == 0 ) return NULL;
        preOrder = preorder;
        inOrder = inorder;
        TreeNode* root = new TreeNode(preOrder[0]);
        int length = preorder.size();
        helper(root,0,length-1,0,length-1);
        return root;
    }
};

