        vector sortedNums = nums;
        //将nums排序并存为一个新的数组，数的大小就可以由位置来体现，解决了数为负数以及数很大的问题
        sort(sortedNums.begin(),sortedNums.end());
        memset(c,0,sizeof(c));
        int length = nums.size();
