        for(int i=0;i findPos(vector>& board){
        for(int i=0;i>& board){
        if(isFull(board)){
            ans = board;
            return;
        }
        //找到第一个没有填的位置
        pair pos = findPos(board);
        int posi = pos.first;
        int posj = pos.second;
        for(int num=1;num>& board) {
        //硬暴力来一发试试
        memset(row,0,sizeof(row));
        memset(col,0,sizeof(col));
        memset(block,0,sizeof(block));
        for(int i=0;i 48){
                    row[i][board[i][j] - 48] = 1;
                    col[j][board[i][j] - 48] = 1;
                    block[i/3][j/3][board[i][j] - 48] = 1;
                }

            }
        }
        dfs(board);
        board = ans; 
