        multiset curSet;
        int left = 0;
        int right = 0;
        int ans = 0;
        int length = nums.size();
        while(right ans?right-left+1:ans;
            }
            else
            {
                left++;
                curSet.erase(curSet.find(nums[left-1]));
            }
            right++;
        }
