    void dfs(int x ,int y, int type, vector>& heights ) // 1è¡¨ç¤ºpacific, 2è¡¨ç¤ºatlantic
    {
        vis[x][y] = 1;
        if(type == 1)
            ocean[x][y].pacific = 1;
        else 
