        bool hasLeft = false;
        for(int i=0;i=0 && m=0 && n= heights[x][y]){
                hasLeft = true;
                if(type == 1){
                    dfs(m,n,1,heights);
                }else{
                    dfs(m,n,2,heights);
                }
            }
        }
        return;
    }

    vector> pacificAtlantic(vector>& heights) {
        vector> ans;
        row = heights.size();
        col = heights[0].size();
        for(int i=0;i temp = {i,j};
                    ans.push_back(temp);
                }
            }
        }

        return ans;
    }
};

