/**
 * Definition for singly-linked list.
 * struct ListNode {
 *     int val;
 *     ListNode *next;
 *     ListNode(int x) : val(x), next(NULL) {}
 * };
 */

 //ä½¿ç”¨æ ˆ
class Solution {
public:
    ListNode* addTwoNumbers(ListNode* l1, ListNode* l2) {
        ListNode* ans = NULL;
        stack s1;
        stack s2;
        while(l1 != NULL)
        {
            s1.push(l1->val);
            l1 = l1 ->next;
        }
        while(l2 != NULL)
        {
            s2.push(l2->val);
            l2 = l2 ->next;
        }

        int c = 0;
        while(!s1.empty() || !s2.empty() || c!=0)
        {
            int add1 = 0;
            int add2 = 0;

            if(!s1.empty())
            {
                add1 += s1.top();
                s1.pop();
            } 
            if(!s2.empty())
            {
                add2 += s2.top();
                s2.pop();
            }

            int sum = add1 +add2 + c;
            c = 0;
            while(sum>=10)
            {
                c++;
                sum -= 10;
            }
            ListNode* tmpNode  =new ListNode();
            tmpNode->val = sum;
            tmpNode->next = ans;
            ans = tmpNode;
        }  
    return ans;        

    }
};

