/**
 * Definition for singly-linked list.
 * struct ListNode {
 *     int val;
 *     ListNode *next;
 *     ListNode(int x) : val(x), next(NULL) {}
 * };
 */
class Solution {
public:
    pair reverseList(ListNode* start, ListNode* end)
    {
        vector recoredPrev;
        ListNode* cur = start;
        while(cur != end)
        {
            recoredPrev.push_back(cur);
            cur = cur->next;
        }
        cur = start->next;
        for(auto prev:recoredPrev)
        {
            ListNode* temp = cur->next;
            cur->next = prev;
            cur = temp;
        }
        start->next = NULL;

        return make_pair(end,start); //è¿˜æ˜¯ä¸äº¤æ¢äº†ï¼Œç›´æ¥è¿”å›å‡ºå»åœ¨å¤–é¢æ”¹å§ 
    }
    ListNode* reverseKGroup(ListNode* head, int k) {
        ListNode* prev = NULL;
        ListNode* start;
        ListNode* cur;
        ListNode* end;
        int cnt = 0;

        cur = head;
        start = head;
        prev = NULL;
        int flag = 0;
        while(cur != NULL)
        {
            cnt++;
            if(cnt == k)
            {
                end = cur;
                cur = cur->next;
                pair ret = reverseList(start,end);
                start = ret.first;
                //***********å¤„ç†ä¸€ä¸‹head********
                if(flag==0)
                {
                    head = start;
                    flag = 1;
                }
                //***********
                end = ret.second;
                end->next = cur;
                if(prev != NULL)
                    prev->next = start;
                prev = end;
                start = cur;
                cnt=0;
            }
            else
            {
                cur = cur->next;
            }

        }    
        return head;
    }
};

