class Solution {
public:
    int findDuplicate(vector& nums) {
       //å¿«æ…¢æŒ‡é’ˆ
       int length = nums.size();
       int slow = 0;
       int fast = 0;
       do{
           slow = nums[slow];
           fast = nums[nums[fast]];
       }while(slow != fast);
       slow = 0;
       while(1)
       {
           if(slow == fast)
           {
               return slow;
           }
           slow = nums[slow];
           fast = nums[fast];
       }
    }
};

