class Solution {
public:
    int canCompleteCircuit(vector& gas, vector& cost) {
        //æ¨¡æ‹Ÿå¿…è¶…æ—¶ï¼Ÿ
        int len = gas.size();
        int curGas = 0;
        for(int start = 0;start = cost[start]){
                curGas -= cost[start];
            }else{
                continue;
            }
            int curPos = (start+1)%len;
            while(curPos != start){
                curGas += gas[curPos];
                if(curGas >= cost[curPos]){
                    curGas -= cost[curPos];
                }
                else{
                    break;
                }
                curPos = (curPos +1)%len;
            }
            if(curPos == start) return start;
        }
        return -1;
    }
};

