class Solution {
public:
    bool reachingPoints(int sx, int sy, int tx, int ty) {
        while(tx > sx && ty > sy){
            if(tx > ty){
                tx = tx % ty;
            }
            else if( ty > tx){
                ty = ty%tx;
            }
            else{
                break;
            }
        }
        cout sy && (ty-sy)%tx == 0){
                return true;
            }
            return false;
        }
        else if(sy == ty){
            if(tx > sx && (tx-sx)%ty == 0){
                return true;
            }
            return false;
        }
        else{
            // sxä¸ç­‰äºtxï¼Œ syä¸ç­‰äºty
            return false;
        }
    }
};

