class Solution {
public:
    int MergeSort(int l, int r, vector& nums)
    {  
        if(r-l == 0)
        {
            return 0;
        }
        //å…ˆæ±‚å·¦å³ä¸¤è¾¹ä¸­å…ˆæ±‚å‡ºæ¥çš„é€†åºå¯¹
        int middle = (l+r)/2;
        int reversePairNum = MergeSort(l,middle,nums) + MergeSort(middle+1,r,nums);
        //å½“å·¦å³ä¸¤è¾¹çš„numséƒ½æ’å¥½åºäº†ï¼Œå¯ä»¥è®¡ç®—ç»„åˆæ—¶äº§ç”Ÿçš„é€†åºå¯¹
        vector temp;
        int lPointer = l;
        int rPointer = middle+1;
        int currentAdd = 0;
        while(!(lPointer>middle && rPointer>r))
        {
            if(lPointer > middle)
            {
               temp.push_back(nums[rPointer]);
               rPointer ++;
            }
            else if(rPointer > r)
            {
                reversePairNum += currentAdd;
                temp.push_back(nums[lPointer]);
                lPointer++;
            }
            else
            {
                if(nums[lPointer] > nums[rPointer] )
                {
                    currentAdd++;
                    temp.push_back(nums[rPointer]);
                    rPointer++;
                }
                else
                {
                    reversePairNum += currentAdd;
                    temp.push_back(nums[lPointer]);
                    lPointer++;
                }
            }

        }
        //å°†numå˜æˆæ’åºåçš„num
        for(int i=0;i& nums) {
        if(nums.size() == 0)
            return 0;
        return MergeSort(0,nums.size()-1,nums);
    }
};




