class Solution {
public:
    int lengthOfLongestSubstring(string s) {
        int left =0;
        int right = 0;
        int length = s.length();
        bool isLongest = 1;
        int max = 1;
        int flag[200];
        if(length == 0)
            return 0;
        while( !( (right == (length-1))  && isLongest))
        {
            if(isLongest)
            {
                if(right-left+1 > max)
                    max = right-left+1;
                right = (right+1 == length)?right:right+1;
            }
            else
            {
                left++;
            }
            memset(flag,0,sizeof(flag));
            isLongest = 1;
            for(int i=left;i 1)
                {
                    isLongest = 0;
                    break;
                }
            }
        }
        return (right-left+1)>max?(right-left+1):max;
    }
};


