class Solution {
public:
    void swap(int* mem,int i,int j){
        int temp = mem[i];
        mem[i] = mem[j];
        mem[j] = temp;
    }

    int minSwaps(vector>& grid) {
        int len = grid.size();
        int mem[200];
        memset(mem,0,sizeof(mem));
        for(int i=0;i= len-i-1) continue;
            int swapRow = -1;
            for(int j=i+1;j= len-i-1){
                    swapRow = j;
                    break;
                }
            }
            if(swapRow == -1) return -1;
            //æ‰¾åˆ°äº¤æ¢çš„è¡Œï¼Œå¼€å§‹äº¤æ¢
            for(int j=swapRow;j>=i+1;j--){
                swap(mem,j,j-1);
                ans++;
            }
        }
        return ans;

    }
};

